INSERT INTO `wp2s_options` VALUES("150", "theme_personage_options", "a:107:{s:7:\"bg-type\";s:1:\"2\";s:10:\"body-solid\";s:7:\"#ffffff\";s:12:\"body-texture\";s:1:\"7\";s:9:\"logo-type\";s:1:\"1\";s:4:\"logo\";s:0:\"\";s:10:\"title-name\";s:9:\"Thi Huynh\";s:14:\"logo-transform\";s:0:\"\";s:9:\"font-logo\";s:6:\"Roboto\";s:13:\"fontsize-logo\";s:2:\"25\";s:7:\"favicon\";s:71:\"http://dearfuturecompany.com/wp-content/uploads/2017/03/downloaad-3.png\";s:11:\"print-state\";s:4:\"link\";s:10:\"print-link\";s:73:\"http://dearfuturecompany.com/wp-content/uploads/2017/03/CV-2017_web-1.pdf\";s:9:\"animation\";s:1:\"1\";s:9:\"small-nav\";s:1:\"1\";s:12:\"intro-height\";s:3:\"650\";s:9:\"bio-title\";s:16:\"Dear HR manager,\";s:8:\"bio-text\";s:385:\"My name is Thi and I\'m studying Digital Maketing & Sales Management at HEC - ULg. I\'m looking for an internship at your company for next February 2018.\r\nI have a good knowledge in branding, Goolge Adwords, SEO, Tableau & basic experience in HTML, SAS & design. I learn fast & work hard. I hope you would seriously consider taking me under your wing next spring 2018!\r\nYours faithfully,\";s:9:\"bio-image\";s:109:\"http://dearfuturecompany.com/wp-content/uploads/2017/03/1512065_10152394019899799_6859750506421254345_o-3.jpg\";s:15:\"signature-image\";s:83:\"http://dearfuturecompany.com/wp-content/uploads/2017/03/220068843575718090216-1.png\";s:15:\"background-type\";s:5:\"image\";s:14:\"color-detector\";s:0:\"\";s:9:\"bg-images\";a:1:{i:0;s:80:\"http://dearfuturecompany.com/wp-content/uploads/2017/03/apple-1868496_1920-1.jpg\";}s:12:\"bg-video-mp4\";s:0:\"\";s:13:\"bg-video-webm\";s:0:\"\";s:12:\"bg-video-ogg\";s:0:\"\";s:15:\"bg-video-poster\";s:0:\"\";s:12:\"overlay-type\";s:8:\"gradient\";s:15:\"overlay-opacity\";s:2:\"70\";s:15:\"overlay-texture\";s:0:\"\";s:13:\"overlay-color\";s:7:\"#000000\";s:17:\"overlay-gradient1\";s:7:\"#313332\";s:17:\"overlay-gradient2\";s:7:\"#1faac2\";s:13:\"portfolio-num\";s:2:\"10\";s:16:\"portfo